SELECT c.oid AS table_oid, c.relname AS table_name, a.attnum AS column_number, a.attname AS column_name, t.oid AS type_oid, t.typname AS type_name, t.typlen AS type_length, t.typbyval AS type_by_value, t.typalign AS type_alignment, t.typstorage AS type_storage, a.atttypmod AS type_modifier, a.attnotnull AS not_null, pg_catalog.format_type(a.atttypid, a.atttypmod) AS full_data_type FROM pg_catalog.pg_class c JOIN pg_catalog.pg_attribute a ON a.attrelid = c.oid JOIN pg_catalog.pg_type t ON a.atttypid = t.oid WHERE c.relname = 'power_shaft_sensor' AND a.attnum > 0 AND NOT a.attisdropped ORDER BY a.attnum
